mapper.select:
SELECT id, postId, userId, contents, registerDate, isAdopted
FROM posts_request
WHERE postID = #{postId}

SELECT id, postId, userId, contents, registerDate, isAdopted
FROM posts_request
WHERE postID = #{postId}
AND userId = #{userId}

mapper.delete:
DELETE FROM posts_like
WHERE postId = #{postId}
AND userId = #{userId}
